<?php set_time_limit(0);include '../config.php';header('Content-Type: text/html; charset=utf-8');$shares = array(0, 500, 1000, 1500, 2000, 3000, 5000, 10000);$q = mysql_query("SELECT * FROM `vipshare` ORDER BY RAND() LIMIT 10");while($raw = mysql_fetch_assoc($q)){$idfb = $raw['idfb'];$tongshare = $raw['soshare'];$limit = $shares[$raw['goi']];$user = $raw['idfb'];//echo $idfb;$tt = mysql_query("SELECT * FROM `tokenpage` ORDER BY RAND() LIMIT $tongshare");while($row = mysql_fetch_assoc($tt)){$token = $row['token'];//echo $token;//$accounts = json_decode(cURL('https://graph.facebook.com/me/accounts?access_token=' . $token),true);$feed = json_decode(cURL('https://graph.facebook.com/'.$idfb.'/feed?access_token='.$token.'&limit=1'),true); $count = $feed['data'][0]['shares']['count'];if($count < $limit){     	for($i=1;$i<=count($feed['data']);$i++){  	echo 'Tổng Share <b style="color:green">'.$count.'</b> /  	<b style="color:red">'.$shares[$raw['goi']].'</b> - Đang Chạy Share - Tình Trạng  	<b style="color:blue">' . cURL('https://graph.facebook.com/' . $feed['data'][$i-1]['id'] . '/sharedposts?method=post&access_token='.$token) . '</b><br/>';				}}}}$infos = mysql_fetch_array(mysql_query("SELECT * FROM `vipshare`"));if (time() > $infos['time']) {mysql_query("DELETE FROM `vipshare` WHERE `time` = `". $infos['time']."` < ".time()."");}/* == Hàm get == */function cURL ($url) {    $data = curl_init();    curl_setopt($data, CURLOPT_RETURNTRANSFER, 1);    curl_setopt($data, CURLOPT_URL, $url);    $hasil = curl_exec($data);    curl_close($data);    return $hasil;} ?> 